import { app, BrowserWindow, ipcMain } from 'electron';
import ProductDatabase from './database/productDb';
import CategoryDatabase from './database/categoryDb';
import SupplierDatabase from './database/supplierDb';
import { Product, ProductFilters } from './types/product';
import { Category, CategoryFilters } from './types/category';
import { Supplier, SupplierFilters } from './types/supplier';

// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  app.quit();
}

// Initialize databases
let productDb: ProductDatabase;
let categoryDb: CategoryDatabase;
let supplierDb: SupplierDatabase;

const createWindow = (): void => {
  // Create the browser window.
  const mainWindow = new BrowserWindow({
    height: 800,
    width: 1200,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
      nodeIntegration: false,
      contextIsolation: true,
    },
  });

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  // Open the DevTools.
  mainWindow.webContents.openDevTools();
};

// Initialize databases and IPC handlers
const initializeApp = () => {
  productDb = new ProductDatabase();
  categoryDb = new CategoryDatabase();
  supplierDb = new SupplierDatabase();
  setupIpcHandlers();
  createWindow();
};

// Setup IPC handlers for product operations
const setupIpcHandlers = () => {
  // Create product
  ipcMain.handle('product:create', async (_, productData) => {
    try {
      return await productDb.createProduct(productData);
    } catch (error) {
      throw error;
    }
  });

  // Get all products with filters
  ipcMain.handle('product:getAll', async (_, filters?: ProductFilters) => {
    try {
      return await productDb.getAllProducts(filters);
    } catch (error) {
      throw error;
    }
  });

  // Get product by ID
  ipcMain.handle('product:getById', async (_, id: string) => {
    try {
      return await productDb.getProductById(id);
    } catch (error) {
      throw error;
    }
  });

  // Update product
  ipcMain.handle('product:update', async (_, id: string, updates: Partial<Product>) => {
    try {
      return await productDb.updateProduct(id, updates);
    } catch (error) {
      throw error;
    }
  });

  // Delete product
  ipcMain.handle('product:delete', async (_, id: string) => {
    try {
      return await productDb.deleteProduct(id);
    } catch (error) {
      throw error;
    }
  });

  // Get product statistics
  ipcMain.handle('product:getStats', async () => {
    try {
      return await productDb.getProductStats();
    } catch (error) {
      throw error;
    }
  });

  // Get low stock products
  ipcMain.handle('product:getLowStock', async () => {
    try {
      return await productDb.getLowStockProducts();
    } catch (error) {
      throw error;
    }
  });

  // Category operations
  ipcMain.handle('category:create', async (_, categoryData) => {
    try {
      return await categoryDb.createCategory(categoryData);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('category:getAll', async (_, filters?: CategoryFilters) => {
    try {
      return await categoryDb.getAllCategories(filters);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('category:getById', async (_, id: string) => {
    try {
      return await categoryDb.getCategoryById(id);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('category:update', async (_, id: string, updates: Partial<Category>) => {
    try {
      return await categoryDb.updateCategory(id, updates);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('category:delete', async (_, id: string) => {
    try {
      const category = await categoryDb.getCategoryById(id);
      if (!category) return false;

      // Update products that use this category
      const products = await productDb.getAllProducts({ category: category.name });
      for (const product of products) {
        await productDb.updateProduct(product.id, { category: 'Sin categorÃ­a' });
      }

      return await categoryDb.deleteCategory(id);
    } catch (error) {
      throw error;
    }
  });

  // Supplier operations
  ipcMain.handle('supplier:create', async (_, supplierData) => {
    try {
      return await supplierDb.createSupplier(supplierData);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('supplier:getAll', async (_, filters?: SupplierFilters) => {
    try {
      return await supplierDb.getAllSuppliers(filters);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('supplier:getById', async (_, id: string) => {
    try {
      return await supplierDb.getSupplierById(id);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('supplier:update', async (_, id: string, updates: Partial<Supplier>) => {
    try {
      return await supplierDb.updateSupplier(id, updates);
    } catch (error) {
      throw error;
    }
  });

  ipcMain.handle('supplier:delete', async (_, id: string) => {
    try {
      const supplier = await supplierDb.getSupplierById(id);
      if (!supplier) return false;

      // Update products that use this supplier
      const products = await productDb.getAllProducts();
      const affectedProducts = products.filter(p => p.supplier === supplier.name);
      for (const product of affectedProducts) {
        await productDb.updateProduct(product.id, { supplier: undefined });
      }

      return await supplierDb.deleteSupplier(id);
    } catch (error) {
      throw error;
    }
  });
};

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', initializeApp);

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

